const NavContext = React.createContext()

class NavProvider extends Component {
    state = { 
        greeting: 'Hello World'
     }
    render() { 
        return (  
                <NavContext.Provider>
                    {this.props.children}
                </NavContext.Provider>
        );
    }
}
export const NavConsumer = C => props => {
    <NavContext.Consumer>
        {value=><C {...props}{...value}/>}
    </NavContext.Consumer>
}

export default NavProvider
